			//get float parameters array
			public string[] UintParametersArray
			{
				get
				{
					//at least one parameter
					if (uintParameters.Keys.ToArray() == null || uintParameters.Keys.ToArray().Length == 0)
					{
						uintParameters.Add("newUint", 0);
					}

					return uintParameters.Keys.ToArray();
				}
			}

			//get float parameters array
			public string[] FloatParametersArray
			{
				get
				{
					//at least one parameter
					if (floatParameters.Keys.ToArray() == null || floatParameters.Keys.ToArray().Length == 0)
					{
						floatParameters.Add("newFloat", 0);
					}

					return floatParameters.Keys.ToArray();
				}
			}

			//get bool parameters array
			public string[] BoolParametersArray
			{
				get
				{
					//at least one parameter
					if (boolParameters.Keys.ToArray() == null || boolParameters.Keys.ToArray().Length == 0)
					{
						boolParameters.Add("newBool", false);
					}

					return boolParameters.Keys.ToArray();
				}
			}

			//get trigger parameters array
			public string[] TriggerParametersArray
			{
				get
				{
					//at least one parameter
					if (triggerParameters.Keys.ToArray() == null || triggerParameters.Keys.ToArray().Length == 0)
					{
						triggerParameters.Add("newTrigger", false);
					}

					return triggerParameters.Keys.ToArray();
				}
			}

			//Init avatar animator
			public List<AvatarAnimationLayer> InitAvatarAnimator(AvatarBodyAnimController bodyAnimController,
				AnimationCallback[] animationCallbacks = null, string[] actualPath = null)
			{
				this.bodyAnimController = bodyAnimController;
				List<AvatarAnimationLayer> animationLayers = new List<AvatarAnimationLayer>();

				//if have actualPath, load external animation
				if (actualPath != null)
				{
					foreach (var path in actualPath)
					{
						bodyAnimController.owner.owner.LoadAllAnimationsExtern(path);
					}
				}

				//Init all animation state nodes callback event
				if (animationCallbacks != null && animationCallbacks.Length > 0)
				{
					foreach (var callback in animationCallbacks)
					{
						callback.animationState.animationCallback = callback;
					}
				}

				//get animation
				string animationString = bodyAnimController.owner.owner.GetAnimationNames();
				loadedAnimations = ParseAnimations(animationString);
				Debug.Log("AvatarAnimator Init Animations :" + animationString);

				for (int i = 0; i < TriggerParametersArray.Length; i++)
				{
					triggerParameters[TriggerParametersArray[i]] = false;
				}

				SetAnimationParameter();

				//Init Layer and Node
				foreach (var layer in layers)
				{
					if (layer == null) continue;
					//Create new avatar animation layer
					AvatarAnimationLayer animationLayer =
						bodyAnimController.CreateAnimationLayerByName(layer.layerName);
					//Init new Avatar animation layer
					layer.InitAnimationLayer(animationLayer);
					//add layer to animationLayers
					animationLayers.Add(animationLayer);
				}

				return animationLayers;
			}

			//Set Animation Parameter
			public void SetAnimationParameter()
			{
				if (bodyAnimController == null) return;
				//Init float and bool Parameters
				foreach (var item in uintParameters)
				{
					bodyAnimController.SetAnimationParameterUIntByName(item.Key, item.Value);
				}

				foreach (var item in floatParameters)
				{
					bodyAnimController.SetAnimationParameterFloatByName(item.Key, item.Value);
				}

				foreach (var item in boolParameters)
				{
					bodyAnimController.SetAnimationParameterBoolByName(item.Key, item.Value);
				}

				foreach (var item in triggerParameters)
				{
					bodyAnimController.SetAnimationParameterBoolByName(item.Key, item.Value);
				}
			}

			//Parse json to list
			List<string> ParseAnimations(string animations)
			{
				List<string> result = new List<string>();
				var tempList = JsonConvert.DeserializeObject<List<System.Object>>(animations);
				if (tempList != null)
					foreach (var item in tempList)
					{
						result.Add(item.ToString());
					}

				return result;
			}

#if UNITY_EDITOR
			private void OnValidate()
			{
				if (Application.isPlaying)
				{
					bodyAnimController.owner.StopCoroutine(SetTriggerValue());
					bodyAnimController.owner.StartCoroutine(SetTriggerValue());
				}

				SetAnimationParameter();
				UpdateStateParameters();
			}

			private void UpdateStateParameters()
			{
				for (int i = 0; i < layers.Count; i++)
				{
					for (int j = 0; j < layers[i].nodes.Count; j++)
					{
						if (layers[i].nodes[j] is AvatarAnimationStateNode)
						{
							(layers[i].nodes[j] as AvatarAnimationStateNode).UpdateTransitionParameter();
						}

						if (layers[i].nodes[j] is AvatarBlendTreeStateNode)
						{
							(layers[i].nodes[j] as AvatarBlendTreeStateNode).UpdateStringDropDownStringArray();
						}
					}
				}
			}

			//Set Trigger value to false
			private IEnumerator SetTriggerValue()
			{
				yield return waitForSeconds;
				for (int i = 0; i < TriggerParametersArray.Length; i++)
				{
					triggerParameters[TriggerParametersArray[i]] = false;
				}
			}
#endif
		}
